mapper.select:
SELECT 
FROM xxl_mq_message AS t
WHERE t.`id` = #{id}

SELECT 
FROM xxl_mq_message AS t
WHERE t.`topic` = #{topic}

        AND t.status = #{status}


        AND t.effect_time  >=  #{effectTimeStart}


        AND t.effect_time  <=  #{effectTimeEnd}

ORDER BY t.`effect_time` DESC
LIMIT #{offset}, #{pagesize}

SELECT count(1)
FROM xxl_mq_message AS t
WHERE t.`topic` = #{topic}

        AND t.status = #{status}


        AND t.effect_time  >=  #{effectTimeStart}


        AND t.effect_time  <=  #{effectTimeEnd}

SELECT 
FROM xxl_mq_message AS t
WHERE t.`topic` = #{topic}
    AND t.`status` in

        #{item}

ORDER BY t.`id` ASC
LIMIT #{pageSize}
